package operater;

public class OPeraterEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		
		num++;
		System.out.println(num);
		
		int num2 = 20;
		
		num2--;
		System.out.println(num2);
		
		
		//복합 대입 연산자 연습
		int va1 =100;
		System.out.println(va1+=1);
		System.out.println(va1-=1);
		System.out.println(va1*= 10);
		System.out.println(va1/= 10);
		
	}

}
